    // }

    state = {
        image_url: '',
        tracks: '',
        description: '',
        name: '',
        owner: ''
    }

    componentDidMount() {
        this.getSinglePlaylist();
    }

    getSinglePlaylist = () => {
        const { params } = this.props.match;
        axios
            .get(`http://localhost:4000/api/playlists/${params.id}`)
            .then(responseFromAPI => {
                this.setState({
                    image_url: responseFromAPI.data.body.images[0].url,
                    tracks: responseFromAPI.data.body.tracks
                })
            })
            .catch(err => console.log(err))
    }

//    getAllTracks = () => {
//        this.getSinglePlaylist()
//        .then(playlist => {
//            this.setState
//        })
//    }

    render() {

        return (
            <div>
                <img src={this.state.images} alt='playlist-cover' />
                <h2>{this.state.name}</h2>
                <p>{this.state.description}</p>
            </div>
        )
    }
}
