import React, { Component } from 'react'
import axios from 'axios'

export default class Details extends Component {
    state = {}

    componentDidMount() {
        this.getSinglePlaylist();
    }

    getSinglePlaylist = () => {
        const { params } = this.props.match;
        axios
            .get(`http://localhost:4000/api/playlists/${params.id}`)
            .then(responseFromAPI => {
                // console.log(responseFromAPI.data.body)
                this.setState(responseFromAPI.data.body)
            })
            .catch(err => console.log(err))
    }

    render() {
        const { images, name, tracks, owner, description, followers } = this.state;
        return (
            <div>
                <img src={images.url} alt='playlist-cover' />
            </div>
        )
    }
}
